import React, {Fragment, useEffect, useState} from "react";
import { Tabs, TabList, TabPanel, Tab } from "react-tabs";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Button, Col, Form, Input, Label, Row } from "reactstrap";
import {getApiConfig} from "../../helpers";
import axios from "axios";
import {toast} from "react-toastify";

const Tabset = (objectInfo = null) => {
	const [startDate, setstartDate] = useState(new Date());
	const [endDate, setendDate] = useState(new Date());
	const [loadedPromotion, setLoadedPromotion] = useState({});

	const handleStartDate = (date) => {
		setstartDate(date);
	};

	const handleEndDate = (date) => {
		setendDate(date);
	};

	const clickActive = (event) => {
		document.querySelector(".nav-link").classList.remove("show");
		event.target.classList.add("show");
	};

	const handleSubmit = async (e) => {
		e.preventDefault();

		const formData = new FormData(e.target);

		// Convert FormData to a plain object
		let formObject = {};
		formData.forEach((value, key) => {
			formObject[key] = value;
		});

		let response;

		if (objectInfo?.id)	{
			response = await axios.patch(`${getApiConfig().baseUrl}/promotions/${objectInfo.id}`, formObject, {headers: getApiConfig().headers});
		} else {
			response = await axios.post(`${getApiConfig().baseUrl}/promotions`, formObject, {headers: getApiConfig().headers});
		}

		if (response.status === 201 || response.status === 200) {
			toast.success('Promotia a fost salvata cu succes!');
			setTimeout(() => {window.location.href = '/coupons/list-coupons'}, 2000);
		} else {
			toast.error('A aparut o eroare la salvarea promotiei!');
		}
	}

	const getPromotions = async (id) => {
		const response = await axios.get(`${getApiConfig().baseUrl}/promotions/${id}`, {headers: getApiConfig().headers});
		if (response?.data) {
			console.log(response.data);
			setLoadedPromotion(response.data);
		}

	}

	useEffect(() => {
	   const params = new URLSearchParams(window.location.search);
	   const id = params.get('id');
	   if (id) {
		   getPromotions(id);
	   }
	}, []);

	return (
		<Fragment>
			<Tabs>
				<TabList className="nav nav-tabs tab-coupon">
					<Tab className="nav-link" onClick={(e) => clickActive(e)}>
						General
					</Tab>
					{/*<Tab className="nav-link" onClick={(e) => clickActive(e)}>*/}
					{/*	Restriction*/}
					{/*</Tab>*/}
					{/*<Tab className="nav-link" onClick={(e) => clickActive(e)}>*/}
					{/*	Usage*/}
					{/*</Tab>*/}
				</TabList>

				<TabPanel>
					<div className="tab-pane fade active show">
						<Form className="needs-validation" noValidate="" onSubmit={handleSubmit}>
							<h4>General</h4>
							<Row>
								<Col sm="12">
									<div className="form-group row">
										<Label className="col-xl-3 col-md-4">
											<span>*</span> Nume
										</Label>
										<div className="col-md-7">
											<Input
												className="form-control"
												id="name"
												name={"name"}
												type="text"
												defaultValue={loadedPromotion?.name}
												required=""
											/>
										</div>
									</div>
									<div className="form-group row">
										<Label className="col-xl-3 col-md-4">
											<span>*</span> Voucher
										</Label>
										<div className="col-md-7">
											<Input
												className="form-control"
												id="voucherCode"
												name={"voucherCode"}
												defaultValue={loadedPromotion?.voucherCode}
												type="text"
												required=""
											/>
										</div>
										<div className="valid-feedback">
											Please Provide a Valid Coupon Code.
										</div>
									</div>
									<div className="form-group row">
										<Label className="col-xl-3 col-md-4">Start Date</Label>
										<div className="col-md-7">
											<DatePicker
												selected={loadedPromotion?.startDate ?? startDate}
												startDate={loadedPromotion?.startDate ?? startDate}
												name={"startDate"}
												id={"startDate"}
												onChange={handleStartDate}
												defaultValue={loadedPromotion?.startDate}
											/>
										</div>
									</div>
									<div className="form-group row">
										<Label className="col-xl-3 col-md-4">End Date</Label>
										<div className="col-md-7">
											<DatePicker
												selected={loadedPromotion?.endDate ?? endDate}
												endDate={loadedPromotion?.endDate ?? endDate}
												name={"endDate"}
												id={"endDate"}
												onChange={handleEndDate}
											/>
										</div>
									</div>
									<div className="form-group row">
										<Label className="col-xl-3 col-md-4">Descriere</Label>
										<div className="col-md-7">
											<Input
												className="form-control"
												type="text"
												name={"description"}
												id={"description"}
												required=""
											/>
										</div>
									</div>
									<div className="form-group row">
										<Label className="col-xl-3 col-md-4">Procent discount (%)</Label>
										<div className="col-md-7">
											<Input
												className="form-control"
												type="number"
												name={"discount"}
												id={"discount"}
												required=""
											/>
										</div>
									</div>
									<div className="form-group row">
										<Label className="col-xl-3 col-md-4">Status</Label>
										<div className="col-md-7">
											<Label className="d-block">
												<Input
													className="checkbox_animated"
													id="isActive"
													name="isActive"
													type="checkbox"
												/>
												Activeaza pe loc
											</Label>
										</div>
									</div>
								</Col>
							</Row>

							<div className="pull-right">
								<Button type="submit" color="primary">
									Save
								</Button>
							</div>
						</Form>
					</div>
				</TabPanel>
				{/*<TabPanel>*/}
				{/*	<Form className="needs-validation" noValidate="">*/}
				{/*		<h4>Restriction</h4>*/}
				{/*		<div className="form-group row">*/}
